import React from 'react';
import CharacterItem from './CharacterItem';

const CharacterGrid =({isloading, items})=> {
    return isloading? (<h1>Loading</h1>) : (
        <section className="cards">
            {items.map((item)=> (
                <h1>{item.name}</h1>
            ))}
        </section>
    )
}

export default CharacterGrid
